import React from 'react'
import { useState, useEffect } from 'react'
import { Button, ProductsContainer, HeaderTable, SubContainer, Article, Wrapper, ButtonWrapper, ArticleWrapper } from '../styles'

export const SubCategories = () => {
    const [subCategories, setSubCategories] = useState([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        try {
            fetch('http://localhost:3001/subcatergories')
                .then(res => res.json())
                .then(data => {
                    setSubCategories(data)
                    setLoading(false)
                })
        } catch (error) {
            console.log(error)
        }
    }, [])
  return (
    <ProductsContainer>
            <HeaderTable>
                <ArticleWrapper>
                    <Article>Products</Article>
                </ArticleWrapper>
                <ButtonWrapper>
                    <Button>DONE</Button>
                </ButtonWrapper>
            </HeaderTable>
            <SubContainer>
                {loading ? <h1>Loading...</h1> :
                    subCategories.map(subCategory => (
                        <Wrapper key={subCategory.subCategoriesId}>
                            <p>{subCategory.subCategoryName}</p>
                            <input type="checkbox" 
                                name="subCategory"
                                value={subCategory.subCategoryId}
                                onChange={() => {
                                    console.log(subCategory.subCategoryName)
                                }
                                }
                            />
                        </Wrapper>
                    ))
                }
            </SubContainer>
            <Button>ADD PRODUCTS</Button>
        </ProductsContainer>
  )
}
