import { createContext, useContext, useEffect, useState } from "react";

const AppContext = createContext()

export const useAppContext = () => useContext(AppContext)

const AppState = ({children}) => {
    const [products, setProducts] = useState([])
    const [subCategories, setSubCategories] = useState([])
    const [loading, setLoading] = useState(false)

    useEffect(() => {
        setLoading(true)
        try{
        const fetchProducts = async () => {
            const res = await fetch('http://localhost:3001/products')
            const data = await res.json()
            setProducts(data)
            setLoading(false)
        }
        fetchProducts()
        } catch (error) {
        console.log(error)
        }
    }, [])

const fetchSubCategories = async (option) => {
    useEffect(() => {
    setLoading(true)
    try{
        const fetchData = async () => {
        const res = await fetch(`http://localhost:3002/subcatergories`)
        const data = await res.json()
        const filteredData = data.filter(item => item.productId === option)
        setSubCategories(filteredData)
        setLoading(false)
        }
        fetchData()
    } catch (error) {
        console.log(error)
    }
    }, [option])
    return {subCategories}
}


    return (
        <AppContext.Provider value={{products, loading, fetchSubCategories}}>
        {children}
        </AppContext.Provider>
    )
    }
export default AppState
