import React, { useEffect } from 'react'
import { useState } from 'react'
import { Button, ProductsContainer, HeaderTable, SubContainer, Article, Wrapper, ButtonWrapper, ArticleWrapper } from '../styles'


export const Products = () => {
    const [products, setProducts] = useState([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        try {
            fetch('https://api.myjson.com/bins/1h2jq0')
                .then(res => res.json())
                .then(data => {
                    setProducts(data)
                    setLoading(false)
                })
        } catch (error) {
            console.log(error)
        }
    }
        , [])
  return (
    <>
        <ProductsContainer>
            <HeaderTable>
                <ArticleWrapper>
                    <Article>Products</Article>
                </ArticleWrapper>
                <ButtonWrapper>
                    <Button>DONE</Button>
                </ButtonWrapper>
            </HeaderTable>
            <SubContainer>
                <Wrapper>
                    <p>Product 1</p>
                    <input type="checkbox" />
                </Wrapper>
                <Wrapper>
                    <p>Product 2</p>
                    <input type="checkbox" />
                </Wrapper>
            </SubContainer>
            <Button>ADD PRODUCTS</Button>
        </ProductsContainer>

    </>
  )
}
