import { Button, ProductsContainer, HeaderTable, SubContainer, Article, Wrapper, ButtonWrapper, ArticleWrapper } from '../styles'
import { SubCategories } from './SubCategories'
import {TiPlus} from 'react-icons/ti'
import AppState, { useAppContext } from '../AppContext/appContext'
import { FormProvider, useFieldArray, useForm } from 'react-hook-form'
import { Modal } from './Modal';
import { useState } from 'react'


export const Products = () => {
    const { products, loading, setOpenModal, openModal} = useAppContext(AppState)
    const methods = useForm();
    const {register, handleSubmit} = methods;
    useFieldArray({
        control: methods.control,
        name: 'products',
    });
    const [selectedData, setSelectedData] = useState({})

   const onSubmit = (data) => {
         setSelectedData(data)
        setOpenModal(true)
    }

  return (
    <>
        <FormProvider {...methods}>
            <form onSubmit={handleSubmit(onSubmit)}>
                <ProductsContainer>
                    <HeaderTable>
                        <ArticleWrapper>
                            <Article>Products</Article>
                        </ArticleWrapper>
                        <ButtonWrapper>
                            <Button type='submit'>DONE</Button>
                        </ButtonWrapper>
                    </HeaderTable>
                    <SubContainer>
                        {loading ? <h1>Loading...</h1> :
                            products.map(product => (
                                <Wrapper key={product.productId}>
                                    <div style={{display : 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0 1rem', marginBottom: '0.4rem'}}>
                                    <label>{product.productName}</label>
                                    <input type="checkbox" 
                                        name={`product${product.productId}`}
                                        value={product.productName}
                                        {...register(`products.${product.productId}.name`)}
                                    />
                                    </div>
                                    {
                                            <SubCategories productId={product.productId}/>
                                    }
                                </Wrapper>
                            ))
                        }
                    </SubContainer>
                    <Button> <TiPlus />ADD PRODUCTS</Button>
                </ProductsContainer>
            </form>
        </FormProvider>
        {
            !!openModal && 
                <Modal>
                    <h3>Products</h3>
                    <p>{selectedData.products.filter(product => product.name).map(product => product.name).join(', ')}</p>
                    <h3>Subcategories</h3>
                    <p>{selectedData.subCategories.filter(subCategory => subCategory.name).map(subCategory => subCategory.name).join(', ')}</p>
                    <h3>Subproducts</h3>
                    <p>{selectedData.subProducts.filter(subProduct => subProduct.name).map(subProduct => subProduct.name).join(', ')}</p>

                </Modal>

    }

    </>
  )
}
