import React from 'react'
import { useForm } from 'react-hook-form'
import Select from 'react-select'
import AppState, { useAppContext } from '../AppContext/appContext'

export const CreateSubProduct = () => {
    const { products, subCategories } = useAppContext(AppState)
    const { register, handleSubmit, reset } = useForm()
    console.log('subCategories', subCategories)

  return (
    <div>
        <h3>Create Sub Product</h3>
            <form onSubmit={handleSubmit}>
                <label>SubProduct Name
                <input {...register('subProductName', { required: true })} />
                </label>
                <Select 
                    name='productId' 
                    options={products.map(product => ({ value: product.productId, label: product.productName }))}
                    {...register('productId', { required: true })}>

                </Select>
                <Select 
                    name='subCategoryId' 
                    // options={subCategories.map(subCategory => ({ value: subCategory.subCategoryId, label: subCategory.name }))}
                    {...register('subCategoryId', { required: true })}>

                </Select>
                <button type='submit'>Submit</button>
            </form>

    </div>
  )
}
