import React, { useEffect } from 'react'
import { useState } from 'react'
import { Button, ProductsContainer, HeaderTable, SubContainer, Article, Wrapper, ButtonWrapper, ArticleWrapper } from '../styles'
import { SubCategories } from './SubCategories'
import {BsPlusLg} from 'react-icons/ti'

export const Products = () => {
    const [products, setProducts] = useState([])
    const [loading, setLoading] = useState(true)
    const [productId, setProductId] = useState()
    const [checked, setChecked] = useState([])

    useEffect(() => {
        try {
            fetch('http://localhost:3001/products')
                .then(res => res.json())
                .then(data => {
                    setProducts(data)
                    setLoading(false)
                })
        } catch (error) {
            console.log(error)
        }
    }, [])
   const handleOnChange = (e) => {
       setProductId(e.target.value) 

   }
  return (
    <>
        <ProductsContainer>
            <HeaderTable>
                <ArticleWrapper>
                    <Article>Products</Article>
                </ArticleWrapper>
                <ButtonWrapper>
                    <Button>DONE</Button>
                </ButtonWrapper>
            </HeaderTable>
            <SubContainer>
                {loading ? <h1>Loading...</h1> :
                    products.map(product => (
                        <Wrapper key={product.productId}>
                            <div style={{display : 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0 1rem', marginBottom: '0.4rem'}}>
                            <label>{product.productName}</label>
                            <input type="checkbox" 
                                name="product"
                                value={product.productId}
                                onChange={handleOnChange}
                            />
                            </div>
                            {
                              <SubCategories productId={product.productId}/>
                            }
                        </Wrapper>
                    ))
                }
            </SubContainer>
            <Button> <BsPlusLg />ADD PRODUCTS</Button>
        </ProductsContainer>

    </>
  )
}
