import React, { useEffect } from 'react'
import { useState } from 'react'
import { Outlet, useNavigate } from 'react-router-dom'
import { Button, ProductsContainer, HeaderTable, SubContainer, Article, Wrapper, ButtonWrapper, ArticleWrapper } from '../styles'
import { SubCategories } from './SubCategories'


export const Products = () => {
    const [products, setProducts] = useState([])
    const [loading, setLoading] = useState(true)
    const navigate = useNavigate()

    useEffect(() => {
        try {
            fetch('http://localhost:3001/products')
                .then(res => res.json())
                .then(data => {
                    setProducts(data)
                    setLoading(false)
                })
        } catch (error) {
            console.log(error)
        }
    }, [])
   const handleOnChange = (e) => {
       let productId = e.target.value
         navigate(`/subcategories/${productId}`)
   }
  return (
    <>
        <ProductsContainer>
            <HeaderTable>
                <ArticleWrapper>
                    <Article>Products</Article>
                </ArticleWrapper>
                <ButtonWrapper>
                    <Button>DONE</Button>
                </ButtonWrapper>
            </HeaderTable>
            <SubContainer>
                {loading ? <h1>Loading...</h1> :
                    products.map(product => (
                        <Wrapper key={product.productId}>
                            <label>{product.productName}</label>
                            <input type="checkbox" 
                                name="product"
                                value={product.productId}
                                onChange={handleOnChange}
                            />
                            {
                                //if product is checked, then show subcategories
                                product.checked ? <SubCategories/> : null
                            }
                        </Wrapper>
                    ))
                }
            </SubContainer>
            <Button>ADD PRODUCTS</Button>
        </ProductsContainer>

    </>
  )
}
