    display: flex;
    justify-content: space-between;
    padding: 0 1rem .2rem 1rem;
    width: 100%;
`

export const ProductsContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: #00a9d5;
    padding: 1rem;
`
export const SubContainer = styled.div`
    background-color: white;
