import { useEffect, useState } from "react"

export const useFetch = (url, options, loading, setLoading) => {
    const [data, setData] = useState([])

    useEffect(() => {
        setLoading(true)
        try {
            const fetchData = async () => {
                const res = await fetch(url)
                const data = await res.json()
                setData(data.filter(data => data.options == options))
                setLoading(false)
            }
            fetchData()
        } catch (error) {
            console.log(error)
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [url, options])

    return {data, setData}
}
