import React from 'react'
import { useState, useEffect } from 'react'
import { Button, SubCategoriesContainer, HeaderTable, SubCategoriesWrapper, Article, Wrapper, ButtonWrapper, ArticleWrapper, SubContainer } from '../styles'

export const SubCategories = () => {
    const [subCategories, setSubCategories] = useState([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        try {
            fetch('http://localhost:3002/subcatergories')
                .then(res => res.json())
                .then(data => {
                    setSubCategories(data)
                    setLoading(false)
                    //filter subcategories by productId

                })
        } catch (error) {
            console.log(error)
        }
    }, [])
  return (
    <SubCategoriesContainer>
            <HeaderTable>
                <ArticleWrapper>
                    <Article>Select subcategories</Article>
                </ArticleWrapper>
                <ButtonWrapper>
                    <Button>></Button>
                </ButtonWrapper>
            </HeaderTable>
            <SubCategoriesWrapper>
                {loading ? <h1>Loading...</h1> :
                    subCategories.map(subCategory => (
                        <Wrapper key={subCategory.subCategoriesId}>
                            <p>{subCategory.subCategoryName}</p>
                            <input type="checkbox" 
                                name="subCategory"
                                value={subCategory.subCategoryId}
                                onChange={() => {
                                    console.log(subCategory.subCategoryName)
                                }
                                }
                            />
                        </Wrapper>
                    ))
                }
            </SubCategoriesWrapper>
            <Button>ADD PRODUCTS</Button>
        </SubCategoriesContainer>
  )
}
