import React from 'react'
import { FormProvider, useForm } from 'react-hook-form';
import AppState, { useAppContext } from '../AppContext/appContext';

export const MainPage = () => {
    const { products } = useAppContext(AppState)
    const methods = useForm();
  const {register, handleSubmit } = methods;
  return (
    <FormProvider>
        <form onSubmit={handleSubmit((data) => console.log(data))}>
            <select
            {...register('product')}

            >
                <option value="">search</option>
                {products.map(item => (
                    <option key={item.productId} value={item.productName}>{item.productName}</option>
                ))}

            </select>
            {products.map(item => (
                <div key={item.productId}>
                <label >{item.productName}</label>
                <input type="checkbox" name={'product.'+ Math.random().toString(16).slice(2)*3} value={item.productName} {...register('input')}/>
                </div>
            ))}
            <input type='ckeckbox' name='product' {...register('type')}/>
            <input type="submit" />
        </form>
    </FormProvider>
  )
}
