import { useEffect, useState } from "react";

export const useFetchProducts = (url) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setLoading(true);
            try {
                fetch('url')
                    .then(res => res.json())
                    .then(data => {
                        setProducts(data)
                        setLoading(false)
                    })
            } catch (error) {
                console.log(error)
            }
        }, [])

    return { products, loading };
}
