import React from 'react'
import { FormProvider, useForm } from 'react-hook-form';
import AppState, { useAppContext } from '../AppContext/appContext';
import { useFilterData } from '../AppContext/useFilterData';

export const MainPage = () => {
    const { products } = useAppContext(AppState)
    const methods = useForm();
  const {register, handleSubmit } = methods;
  const { data } = useFilterData('http://localhost:3002/subcatergories',products)
  return (
    <FormProvider>
        <form onSubmit={handleSubmit((data) => console.log(data))}>
            <select
            {...register('product')}
            >
                <option value="">search</option>
                {products.map(item => (
                    <option key={item.productId} value={item.productId}>{item.productName}</option>
                ))}

            </select>
            {products.map(item => (
                <div key={item.productId}>
                    <label>{item.productName}</label>
                    <input type="checkbox"
                        name={`product${item.productId}`}
                        value={item.productName}
                        {...register(`product${item.productId}`)}
                    />
                    {
                        data.map(subcategory => (
                            <div key={subcategory.subcategoryId}>
                                <label>{subcategory.subcategoryName}</label>
                                <input type="checkbox"
                                    name={`subcategory${subcategory.subcategoryId}`}
                                    value={subcategory.subcategoryName}
                                    {...register(`subcategory${subcategory.subcategoryId}`)}
                                />
                            </div>
                        ))
                    }

                </div>
            ))}
            <input type="submit" />
        </form>
    </FormProvider>
  )
}
