import React, { useContext, useState } from 'react'
import AppState from '../AppContext/appContext'
import { useFilterData } from '../AppContext/useFilterData'

export const Search = ({data}) => {
  const { setSuggestions, search } = useContext(AppState)
  const [searchValue, setSearchValue] = useState('')

    const onChangeHandler = (searchValue) => search(data, searchValue)


  return (
    <>
        <input 
              style={{with: '100%', height: '2rem', border: '1px solid #ccc', borderRadius: '0.2rem', padding: '0.5rem'}}
              type='search' 
              placeholder='search'
              value={searchValue}
              onChange={(e) => onChangeHandler(e.target.value)}
              />
    </>
  )
}
