import React from 'react'
import { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { Button, SubCategoriesContainer, HeaderTable, SubCategoriesWrapper, Article, Wrapper, ButtonWrapper, ArticleWrapper, SubContainer } from '../styles'

export const SubCategories = ({}) => {
    const [subCategories, setSubCategories] = useState([])
    const [loading, setLoading] = useState(true)
    const params = useParams()
    const productId = params.productId
    useEffect(() => {
        try {
            fetch('http://localhost:3002/subcatergories')
                .then(res => res.json())
                .then(data => {
                    // eslint-disable-next-line array-callback-return
                    setLoading(false)
                    let subCategories = data.filter(subCategory => subCategory.productId == productId)
                    setSubCategories(subCategories)
                })
        } catch (error) {
            console.log(error)
        }
    }, [productId])

  return (
    <SubCategoriesContainer>
            <HeaderTable>
                <ArticleWrapper>
                    <Article>Select subcategories</Article>
                </ArticleWrapper>
                <ButtonWrapper>
                    <Button>up</Button>
                </ButtonWrapper>
            </HeaderTable>
            <SubCategoriesWrapper>
                {loading ? <h1>Loading...</h1> :
                    subCategories.map(subCategory => (
                        <Wrapper key={subCategory.subCategoriesId}>
                            <p>{subCategory.subCategoryName}</p>
                            <input type="checkbox" 
                                name="subCategory"
                                value={subCategory.subCategoryId}
                                onChange={() => {
                                    console.log(subCategory.subCategoryName)
                                }
                                }
                            />
                        </Wrapper>
                    ))
                }
            </SubCategoriesWrapper>
            <Button>ADD PRODUCTS</Button>
        </SubCategoriesContainer>
  )
}
