import { Button, ProductsContainer, HeaderTable, SubContainer, Article, Wrapper, ButtonWrapper, ArticleWrapper } from '../styles'
import { SubCategories } from './SubCategories'
import {TiPlus} from 'react-icons/ti'
import AppState, { useAppContext } from '../AppContext/appContext'
import { FormProvider, useFieldArray, useForm } from 'react-hook-form'
import { Modal } from './Modal';
import { useState } from 'react'
import { SelectionOverview } from './SelectionOverview'


export const Products = () => {
    const { products, loading, setOpenModal, openModal, getSelectedProducts, selectedProducts, setSelectedProducts} = useAppContext(AppState)
    const methods = useForm();
    const {register} = methods;
    useFieldArray({
        control: methods.control,
        name: 'products',
    });
    const handleOnClickDone = () => {
        setOpenModal(true)
    }

    const handleOnClickCheckbox = (e) => {
        getSelectedProducts(e)
    }

  return (
    <>
        <ProductsContainer>
                    <HeaderTable>
                        <ArticleWrapper>
                            <Article>Products</Article>
                        </ArticleWrapper>
                        <ButtonWrapper>
                            <Button onClick={handleOnClickDone}>DONE</Button>
                        </ButtonWrapper>
                    </HeaderTable>
                    <SubContainer>
                        {loading ? <h1>Loading...</h1> :
                            products.map(product => (
                                <Wrapper key={product.productId}>
                                    <div style={{display : 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0 1rem', marginBottom: '0.4rem'}}>
                                    <label>{product.productName}</label>
                                    <input type="checkbox" 
                                        name={`product${product.productId}`}
                                        value={product.productName}
                                        onClick={handleOnClickCheckbox}
                                        {...register(`products.${product.productId}.name`)}
                                    />
                                    </div>
                                    {
                                        selectedProducts.some(item => item.productId === product.productId) && <SubCategories productId={product.productId}/>
                                    }
                                </Wrapper>
                            ))
                        }
                    </SubContainer>
            <Button> <TiPlus />ADD PRODUCT</Button>
        </ProductsContainer>
        {
            !!openModal && 
                <Modal>
                    <SelectionOverview 
                    selectedProducts={selectedProducts}
                    />
                </Modal>
        }

    </>
  )
}
