import React from 'react'
import { useForm } from 'react-hook-form'
import Select from 'react-select'
import AppState, { useAppContext } from '../AppContext/appContext'
import { Button } from '../styles'

export const CreateSubProduct = () => {
    const { products, subCategories } = useAppContext(AppState)
    const { register, 
        handleSubmit: handleSubmit2,
        reset } = useForm()

    const onSubmit = (e, data) => {
        reset()
        e.preventDefault()
        console.log('data', data)
    }

  return (
    <div>
        <h3>Create Sub Product</h3>
            <form onSubmit={handleSubmit2(onSubmit)}>
                <label>SubProduct Name
                <input {...register('subProductName', { required: true })} />
                </label>
                <select {...register('productId', { required: true })}>
                    {products.map(product => (
                        <option key={product.productId} value={product.productId}>{product.productName}</option>
                    ))}
                </select>
                {/* <Select 
                    name='productId' 
                    options={products.map(product => ({ value: product.productId, label: product.productName }))}
                    {...register('productId', { required: true })}>

                </Select>
                <Select 
                    name='subCategoryId' 
                    options={subCategories.map(subCategory => ({ value: subCategory.subCategoryId, label: subCategory.subCategoryName }))}
                    {...register('subCategoryId', { required: true })}>

                </Select> */}
                <Button type='button'>Submit</Button>
            </form>

    </div>
  )
}
